sqlMap.select:

    		/**!mycat:datanode=$db$**/

		SELECT CD.TJRQ,CD.KHDXDH,CD.CDBH,CD.CDMC,CD.CDMS,CD.CDWD,CD.CDDYB FROM MPAS_XT_HYDYCD CD
		WHERE CD.TJRQ=$tjrq$ AND CD.KHDXDH=$khdxdh$ AND CD.CDBH=$cdbh$

sqlMap.insert:

    		/**!mycat:datanode=$db$**/

		insert into MPAS_SZ_CZRZ (tjrq, dlkhdxdh, cdbh, czsj,cdmc, sfscdl)
		values($tjrq$, $dlkhdxdh$, $cdbh$, f_get_timestamp('$czsj$'),'$cdmc$',$sfscdl$)
